import React, { useState, useEffect } from 'react'
import BackendErrorMessage from 'components/backendErrorMessages';


const ArticleForm = ({onSubmit, error, initialValues}) => {
    const [title, setTitle] = useState("");
    const [body, setBody] = useState("");
    const [description, setDescription] = useState("");
    const [tagList, setTagList] = useState("");
    

    const handleSubmit = event => {
        event.preventDafault();
        const article = {
            title,
            description,
            body,
            tagList
        }
        onSubmit(article);
    }

    useEffect(() => {
        if (!initialValues) {
            return;
        }
        console.log('initialValues', initialValues)
        setTitle(initialValues.title);
        setDescription(initialValues.description);
        setBody(initialValues.body);
        setTagList(initialValues.tagList.join(' '));
    }, [initialValues])

    return(
        <div className="editor-page">
            <div className="container page">
                <div className="row">
                    <div className="col-md-10 offset-md-1 col-xs-12">
                        {error && <BackendErrorMessage backendErrors={error}/>}
                        BackendErrorMessage
                        <form onSubmit={handleSubmit}>
                            <fieldset>
                                <fieldset className="form-group">
                                    <input 
                                        type="text" 
                                        className="form-control form-control-lg" 
                                        placeholder="Article title"
                                        value={title}
                                        onChange={e => setTitle(e.target.value)}
                                    />
                                </fieldset>
                                <fieldset className="form-group">
                                    <input 
                                        type="text" 
                                        className="form-control form-control-lg" 
                                        placeholder="What is this article about?"
                                        value={description}
                                        onChange={e => setDescription(e.target.value)}
                                    />
                                </fieldset>
                                <fieldset className="form-group">
                                    <textarea 
                                        className="form-control" 
                                        rows="8" 
                                        placeholder="Write your article (in markdown)"
                                        value={body}
                                        onChange={e => setBody(e.target.value)}
                                    ></textarea>
                                </fieldset>
                                <fieldset className="form-group">
                                    <input 
                                        type="text" 
                                        className="form-control form-control-lg" 
                                        placeholder="Enter tags!"
                                        value={tagList}
                                        onChange={e => setTagList(e.target.value)}
                                    />
                                </fieldset>
                                <fieldset className="form-group">
                                    <button 
                                        type="submit" 
                                        className="btn btn-primary btn-lg pull-xs-right"
                                    >
                                        Publish Article
                                    </button>
                                </fieldset>
                            </fieldset>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ArticleForm;