import { Switch, Route } from 'react-router-dom';
import React from 'react';
import GlobalFeed from 'pages/globalFeed';
import Article from 'pages/article';
import Authentication from 'pages/authentication';
import TagFeed from 'pages/tagFeed/index';
import YourFeed from 'pages/yourFeed/index';
import CreateArticle from 'pages/createArticles/index';
import EditArticle from 'pages/editArticle/index';

export default () => {
    return (
        <Switch>
            <Route path="/" exact component={GlobalFeed}/>
            <Route path="/article/new" component={CreateArticle}/>
            <Route path="/article/:slug/edit" component={EditArticle}/>
            <Route path="/feed" component={YourFeed}/>
            <Route path="/tags/:slug" component={TagFeed}/>
            <Route path="/login" component={Authentication}/>
            <Route path="/register" component={Authentication}/>
            <Route path="/articles/:slug" component={Article}/>
        </Switch>
    )
}
